if (isset($_GET['source'])) {
    if (!is_dir($_GET['source'])) {
		$sourceData = file_get_contents($_GET['source']);
		$type = mime_content_type($_GET['source']);
		if (!preg_match_all('/text/U',$type) == '1') {
			echo basename($_GET['source']).' is not a text or binary. It cannot be opened.';
			exit;
		}
        echo '<textarea style="height: 100%; width: 100%; border: none; top: 0px; left: 0px; bottom: 0px; right: 0px;" readonly>'.htmlentities($sourceData).'</textarea>';
    } else {
        echo $_GET['source'].' is not a file. Its a folder.';
	}
	exit;
}

if (isset($_GET['zip'])) {
	$rootPath = realpath(dirname(__FILE__).'/');
	$folder = $_GET['zip'];
	if ($folder == '.') {
		$zipfolder = 'All';
	} else {
		$zipfolder = $_GET['zip'];
	}
	// Initialize archive object
	$zip = new ZipArchive();
	$zip->open(basename($zipfolder).'.zip', ZipArchive::CREATE | ZipArchive::OVERWRITE);
	
	// Create recursive directory iterator
	/** @var SplFileInfo[] $files */
	$files = new RecursiveIteratorIterator(
		new RecursiveDirectoryIterator($folder),
		RecursiveIteratorIterator::LEAVES_ONLY
	);
	
	foreach ($files as $name => $file)
	{
		// Skip directories (they would be added automatically)
		if (!$file->isDir())
		{
			// Get real and relative path for current file
			$filePath = $file->getRealPath();
			$relativePath = substr($filePath, strlen($rootPath) + 1);
	
			// Add current file to archive
			$zip->addFile($filePath, $relativePath);
		}
	}
	
	// Zip archive will be created only after closing object
	$zip->close();
	$info = '<div class="info">Zipped!</div>';
}

if (isset($_GET['delete'])) {
	$dirPath = $_GET['delete'];
	if (! is_dir($dirPath)) {
        if (unlink($dirPath) == 1) {
			$info = '<div class="info">Deleted!</div>';
		}
    }
    if (file_exists($dirPath)) {
		if (substr($dirPath, strlen($dirPath) - 1, 1) != '/') {
			$dirPath .= '/';
		}
		$files = glob($dirPath . '*', GLOB_MARK);
		foreach ($files as $file) {
			if (is_dir($file) && $file !== $dirPath) {
				echo '<div class="info">Please delete subfolders first!</div>';
			} else {
				unlink($file);
			}
		}
		if (rmdir($dirPath) == 1) {
			$info = '<div class="info">Deleted!</div>';
		}
	}
}
