<script type="text/javascript">
		$(document).ready(function () {
			$('#uploadForm').submit(function (e) {
				if ($('#file').val() && $('#upload').val()) {
					e.preventDefault();
					$('#progress-div').slideDown(900);
					$('#btnSubmit').replaceWith('<div class="la-ball-fall dot-drops"><div></div><div></div><div></div>');
					$(this).ajaxSubmit({
						target: '#targetLayer',
						beforeSubmit: function () {
							$("#progress-bar").width('0%');
						},
						uploadProgress: function (event, position, total, percentComplete) {
							$("#progress-bar").width(percentComplete + '%');
							$("#progress-bar").html('<div id="progress-status">' + percentComplete + ' %</div>');
						},
						success: function () {
							$('div.dot-drops').replaceWith('<input type="submit" id="btnSubmit" value="Submit" class="btnSubmit" />');
							$('#progress-div').slideUp(3000);
						},
						resetForm: true
					});
					return false;
				}
			});
		});
	</script>
	<style>
		#uploadForm {
			background: #929292;
			padding: 10px;
			/*width: 610px;*/
			-webkit-box-sizing: border-box;
			-moz-box-sizing: border-box;
			-ms-box-sizing: border-box;
			box-sizing: border-box;
			display: inline-block;
			vertical-align: middle;
			padding: 2px;
			font-size: 9pt;
			color: #929292;
			background: rgba(0, 0, 0, 0.68);
			box-shadow: 0 0 300px #A3A28B;
			border-radius: 8px;
			color: skyblue;
			margin-top: 12vh;
		}

		#uploadForm label {
			margin: 2px;
			font-size: 1em;
			font-weight: bold;
		}

		.inputBox {
			padding: 5px;
			border-radius: 4px;
			background-color: rgba(0, 0, 0, 0.18);
		}

		#progress-bar {
			background-color: #12CC1A;
			height: 24px;
			color: #FFFFFF;
			width: 0%;
			-webkit-transition: width .3s;
			-moz-transition: width .3s;
			transition: width .3s;
